mapper.insert:
insert into b_publicMember_tbl(m_idx, m_id, m_pw, m_nickname, m_name, m_gender,m_email,m_phone,m_birth,m_regdate,m_auth,m_connect,m_img)
values (b_publicMember_SEQ.nextval, #{m_id},#{m_pw},#{m_nickname}, #{m_name},#{m_gender},#{m_email},#{m_phone},#{m_birth}
		,sysdate,'public',0,'clover.png')

mapper.select:
select * from ( select /*+index_desc(tbl_board pk_board) */ rownum rn, m_idx, m_id, m_nickname, m_name, m_gender, m_email,m_auth,m_phone from ( select * from b_publicmember_tbl where m_auth = 'public' and
m_id like '%'||#{searchName}||'%' m_nickname like '%'||#{searchName}||'%' m_name like '%'||#{searchName}||'%' 1=1
order by m_idx desc)where rownum <= #{nowPage} * #{cntPerPage} ) where rn > (#{nowPage}-1) * #{cntPerPage}
